import React, { useState, useEffect } from 'react';
import './TimeBlocking.css';
import { getTimeBlocks, createTimeBlock, updateTimeBlock, deleteTimeBlock } from '../../utils/api';

const TimeBlockingCalendar = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [view, setView] = useState('month'); // 'month', 'week', 'day'
  const [blocks, setBlocks] = useState([]);
  const [newBlock, setNewBlock] = useState({
    timeSlot: '',
    activity: '',
    category: 'academic',
    description: '',
    date: new Date().toISOString().split('T')[0],
    quadrant: 'urgentImportant'
  });
  const [showSidebar, setShowSidebar] = useState(true);
  const [currentMonth, setCurrentMonth] = useState(new Date());

  const categories = {
    academic: {
      label: 'Academic',
      color: '#4285F4',
      icon: 'ðŸ“š'
    },
    personal: {
      label: 'Personal Development',
      color: '#0F9D58',
      icon: 'ðŸ’»'
    },
    social: {
      label: 'Social & Rest',
      color: '#DB4437',
      icon: 'ðŸ‘¥'
    }
  };

  const quadrants = {
    urgentImportant: {
      label: 'Urgent & Important',
      color: '#FF4444',
      icon: 'ðŸ”´',
      description: 'Schedule immediately (today/tomorrow)'
    },
    importantNotUrgent: {
      label: 'Important but Not Urgent',
      color: '#FFBB33',
      icon: 'ðŸŸ¡',
      description: 'Schedule deep work blocks during peak energy hours'
    },
    urgentNotImportant: {
      label: 'Not Important but Urgent',
      color: '#00C851',
      icon: 'ðŸŸ¢',
      description: 'Delegate or automate (use reminders, group them weekly)'
    },
    notUrgentNotImportant: {
      label: 'Not Important & Not Urgent',
      color: '#CCCCCC',
      icon: 'âšª',
      description: 'Don\'t scheduleâ€”track only if truly necessary'
    }
  };

  const projectCategories = [
    { id: 'assignments', label: 'Assignments/Reports', icon: 'ðŸ“' },
    { id: 'project', label: 'Final Year Project', icon: 'ðŸŽ“' },
    { id: 'research', label: 'Reading/Research', icon: 'ðŸ“š' },
    { id: 'certificate', label: 'Certificate', icon: 'ðŸ†' },
    { id: 'gantt', label: 'Visual Timeline', icon: 'ðŸ“Š' }
  ];

  const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

  useEffect(() => {
    loadTimeBlocks();
  }, [selectedDate]);

  const loadTimeBlocks = async () => {
    try {
      const blocks = await getTimeBlocks();
      setBlocks(blocks);
    } catch (error) {
      console.error('Error loading time blocks:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTimeBlock(newBlock);
      setNewBlock({
        timeSlot: '',
        activity: '',
        category: 'academic',
        description: '',
        date: selectedDate.toISOString().split('T')[0],
        quadrant: 'urgentImportant'
      });
      loadTimeBlocks();
    } catch (error) {
      console.error('Error creating time block:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteTimeBlock(id);
      loadTimeBlocks();
    } catch (error) {
      console.error('Error deleting time block:', error);
    }
  };

  const getDaysInMonth = (date) => {
    if (!date) return [];
    const year = date.getFullYear();
    const month = date.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const days = [];

    // Add padding days from previous month
    for (let i = 0; i < firstDay.getDay(); i++) {
      const prevDate = new Date(year, month, -i);
      days.unshift(prevDate);
    }

    // Add days of current month
    for (let i = 1; i <= lastDay.getDate(); i++) {
      days.push(new Date(year, month, i));
    }

    // Add padding days from next month
    const remainingDays = 42 - days.length; // 6 rows * 7 days
    for (let i = 1; i <= remainingDays; i++) {
      days.push(new Date(year, month + 1, i));
    }

    return days;
  };

  const getWeekDates = (date) => {
    if (!date) return [];
    const week = [];
    const current = new Date(date);
    current.setDate(current.getDate() - current.getDay());

    for (let i = 0; i < 7; i++) {
      week.push(new Date(current.getFullYear(), current.getMonth(), current.getDate() + i));
    }

    return week;
  };

  const formatDate = (date) => {
    if (!date) return '';
    return date.toLocaleDateString('en-US', {
      month: 'long',
      year: 'numeric',
    });
  };

  const isToday = (date) => {
    if (!date) return false;
    const today = new Date();
    return date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear();
  };

  const isSelected = (date) => {
    if (!date || !selectedDate) return false;
    return date.getDate() === selectedDate.getDate() &&
      date.getMonth() === selectedDate.getMonth() &&
      date.getFullYear() === selectedDate.getFullYear();
  };

  const getBlocksForDate = (date) => {
    if (!date) return [];
    return blocks.filter(block => {
      const blockDate = new Date(block.date);
      return blockDate.getDate() === date.getDate() &&
        blockDate.getMonth() === date.getMonth() &&
        blockDate.getFullYear() === date.getFullYear();
    });
  };

  const handleDateClick = (date) => {
    setSelectedDate(date);
  };

  const handleAddBlock = (date) => {
    // TODO: Implement add block functionality
    console.log('Add block for date:', date);
  };

  const navigateMonth = (direction) => {
    const newDate = new Date(selectedDate);
    newDate.setMonth(newDate.getMonth() + direction);
    setSelectedDate(newDate);
  };

  const renderMonthView = () => {
    const days = getDaysInMonth(selectedDate);

    return (
      <div className="calendar-grid">
        {weekDays.map(day => (
          <div key={day} className="weekday-header">{day}</div>
        ))}
        {days.map((date, index) => (
          <div
            key={index}
            className={`calendar-day ${isToday(date) ? 'today' : ''} ${isSelected(date) ? 'selected' : ''}`}
            onClick={() => handleDateClick(date)}
          >
            <div className="day-header">
              <span className="day-number">{date.getDate()}</span>
            </div>
            <div className="day-blocks">
              {getBlocksForDate(date).map((block, i) => (
                <div key={i} className="time-block" style={{ backgroundColor: block.color }}>
                  {block.title}
                </div>
              ))}
            </div>
            <button
              className="add-block-button"
              onClick={(e) => {
                e.stopPropagation();
                handleAddBlock(date);
              }}
            >
              + Add Block
            </button>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="time-blocking-container">
      <div className="calendar-header">
        <h2 className="calendar-title">{formatDate(selectedDate)}</h2>
        <div className="calendar-navigation">
          <button className="calendar-nav-button" onClick={() => navigateMonth(-1)}>
            Previous
          </button>
          <button className="calendar-nav-button" onClick={() => setSelectedDate(new Date())}>
            Today
          </button>
          <button className="calendar-nav-button" onClick={() => navigateMonth(1)}>
            Next
          </button>
        </div>
      </div>
      {renderMonthView()}
    </div>
  );
};

export default TimeBlockingCalendar;
