import axios from 'axios';

const API_BASE_URL = 'http://localhost:3001/api';

// Time Block API calls
export const getTimeBlocks = async () => {
  const response = await axios.get(`${API_BASE_URL}/timeblocks`);
  return response.data;
};

export const createTimeBlock = async (timeBlock) => {
  const response = await axios.post(`${API_BASE_URL}/timeblocks`, timeBlock);
  return response.data;
};

export const updateTimeBlock = async (id, timeBlock) => {
  const response = await axios.put(`${API_BASE_URL}/timeblocks/${id}`, timeBlock);
  return response.data;
};

export const deleteTimeBlock = async (id) => {
  const response = await axios.delete(`${API_BASE_URL}/timeblocks/${id}`);
  return response.data;
};

// Task API calls
export const getTasks = async () => {
  const response = await axios.get(`${API_BASE_URL}/tasks`);
  return response.data;
};

export const createTask = async (task) => {
  const response = await axios.post(`${API_BASE_URL}/tasks`, task);
  return response.data;
};

export const updateTask = async (id, task) => {
  const response = await axios.put(`${API_BASE_URL}/tasks/${id}`, task);
  return response.data;
};

export const deleteTask = async (id) => {
  const response = await axios.delete(`${API_BASE_URL}/tasks/${id}`);
  return response.data;
};

// Pomodoro Session API calls
export const getPomodoroSessions = async () => {
  const response = await axios.get(`${API_BASE_URL}/pomodoro-sessions`);
  return response.data;
};

export const createPomodoroSession = async (session) => {
  const response = await axios.post(`${API_BASE_URL}/pomodoro-sessions`, session);
  return response.data;
};
