import React, { useState, useEffect } from 'react';
import './TimeBlocking.css';
import { getTimeBlocks, createTimeBlock, updateTimeBlock, deleteTimeBlock } from '../../utils/api';

function TimeBlockingCalendar() {
  const [timeBlocks, setTimeBlocks] = useState([]);
  const [newBlock, setNewBlock] = useState({
    timeSlot: '',
    activity: '',
    category: 'academic',
    description: '',
    date: new Date().toISOString().split('T')[0]
  });
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [view, setView] = useState('week'); // 'day', 'week', 'month'

  const categories = {
    academic: {
      label: 'Academic',
      color: '#4285F4',
      icon: 'ðŸ“š'
    },
    personal: {
      label: 'Personal Development',
      color: '#0F9D58',
      icon: 'ðŸ’»'
    },
    social: {
      label: 'Social & Rest',
      color: '#DB4437',
      icon: 'ðŸ‘¥'
    }
  };

  useEffect(() => {
    loadTimeBlocks();
  }, [selectedDate]);

  const loadTimeBlocks = async () => {
    try {
      const blocks = await getTimeBlocks();
      // Filter blocks for the selected date
      const filteredBlocks = blocks.filter(block => block.date === selectedDate);
      setTimeBlocks(filteredBlocks);
    } catch (error) {
      console.error('Error loading time blocks:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTimeBlock(newBlock);
      setNewBlock({
        timeSlot: '',
        activity: '',
        category: 'academic',
        description: '',
        date: selectedDate
      });
      loadTimeBlocks();
    } catch (error) {
      console.error('Error creating time block:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteTimeBlock(id);
      loadTimeBlocks();
    } catch (error) {
      console.error('Error deleting time block:', error);
    }
  };

  const getWeekDates = () => {
    const dates = [];
    const currentDate = new Date(selectedDate);
    const day = currentDate.getDay();
    const diff = currentDate.getDate() - day;
    
    for (let i = 0; i < 7; i++) {
      const date = new Date(currentDate.setDate(diff + i));
      dates.push(date.toISOString().split('T')[0]);
    }
    return dates;
  };

  const renderCalendarView = () => {
    if (view === 'day') {
      return (
        <div className="calendar-day-view">
          <h3>{new Date(selectedDate).toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</h3>
          <div className="time-slots">
            {Array.from({ length: 24 }, (_, i) => (
              <div key={i} className="time-slot">
                <div className="time-label">{`${i.toString().padStart(2, '0')}:00`}</div>
                <div className="time-block-container">
                  {timeBlocks
                    .filter(block => {
                      const [hours] = block.timeSlot.split(':');
                      return parseInt(hours) === i;
                    })
                    .map(block => (
                      <div
                        key={block.id}
                        className="time-block"
                        style={{ backgroundColor: categories[block.category].color }}
                      >
                        <div className="block-header">
                          <span className="category-icon">{categories[block.category].icon}</span>
                          <span className="activity">{block.activity}</span>
                        </div>
                        <div className="block-time">{block.timeSlot}</div>
                        {block.description && <div className="block-description">{block.description}</div>}
                        <button onClick={() => handleDelete(block.id)}>Delete</button>
                      </div>
                    ))}
                </div>
              </div>
            ))}
          </div>
        </div>
      );
    }

    if (view === 'week') {
      const weekDates = getWeekDates();
      return (
        <div className="calendar-week-view">
          <div className="week-header">
            {weekDates.map(date => (
              <div key={date} className="week-day">
                <h4>{new Date(date).toLocaleDateString('en-US', { weekday: 'short' })}</h4>
                <p>{new Date(date).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}</p>
              </div>
            ))}
          </div>
          <div className="week-grid">
            {Array.from({ length: 24 }, (_, hour) => (
              <div key={hour} className="week-row">
                <div className="time-label">{`${hour.toString().padStart(2, '0')}:00`}</div>
                {weekDates.map(date => (
                  <div key={date} className="week-cell">
                    {timeBlocks
                      .filter(block => {
                        const [hours] = block.timeSlot.split(':');
                        return block.date === date && parseInt(hours) === hour;
                      })
                      .map(block => (
                        <div
                          key={block.id}
                          className="time-block"
                          style={{ backgroundColor: categories[block.category].color }}
                        >
                          <div className="block-header">
                            <span className="category-icon">{categories[block.category].icon}</span>
                            <span className="activity">{block.activity}</span>
                          </div>
                          <div className="block-time">{block.timeSlot}</div>
                        </div>
                      ))}
                  </div>
                ))}
              </div>
            ))}
          </div>
        </div>
      );
    }
  };

  return (
    <div className="time-blocking">
      <h2>Time Blocking Calendar</h2>
      
      <div className="calendar-controls">
        <div className="view-selector">
          <button 
            className={view === 'day' ? 'active' : ''} 
            onClick={() => setView('day')}
          >
            Day
          </button>
          <button 
            className={view === 'week' ? 'active' : ''} 
            onClick={() => setView('week')}
          >
            Week
          </button>
        </div>
        
        <input
          type="date"
          value={selectedDate}
          onChange={(e) => setSelectedDate(e.target.value)}
        />
      </div>

      <form onSubmit={handleSubmit} className="time-block-form">
        <input
          type="time"
          value={newBlock.timeSlot}
          onChange={(e) => setNewBlock({ ...newBlock, timeSlot: e.target.value })}
          required
        />
        <input
          type="text"
          value={newBlock.activity}
          onChange={(e) => setNewBlock({ ...newBlock, activity: e.target.value })}
          placeholder="Activity"
          required
        />
        <select
          value={newBlock.category}
          onChange={(e) => setNewBlock({ ...newBlock, category: e.target.value })}
        >
          {Object.entries(categories).map(([key, { label }]) => (
            <option key={key} value={key}>{label}</option>
          ))}
        </select>
        <textarea
          value={newBlock.description}
          onChange={(e) => setNewBlock({ ...newBlock, description: e.target.value })}
          placeholder="Description (optional)"
        />
        <button type="submit">Add Block</button>
      </form>

      {renderCalendarView()}
    </div>
  );
}

export default TimeBlockingCalendar;
