import React, { useState, useEffect } from 'react';
import './TimeBlocking.css';
import { getTimeBlocks, createTimeBlock, updateTimeBlock, deleteTimeBlock } from '../../utils/api';

function TimeBlockingCalendar() {
  const [timeBlocks, setTimeBlocks] = useState([]);
  const [newBlock, setNewBlock] = useState({ timeSlot: '', activity: '' });

  useEffect(() => {
    loadTimeBlocks();
  }, []);

  const loadTimeBlocks = async () => {
    try {
      const blocks = await getTimeBlocks();
      setTimeBlocks(blocks);
    } catch (error) {
      console.error('Error loading time blocks:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTimeBlock(newBlock);
      setNewBlock({ timeSlot: '', activity: '' });
      loadTimeBlocks();
    } catch (error) {
      console.error('Error creating time block:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteTimeBlock(id);
      loadTimeBlocks();
    } catch (error) {
      console.error('Error deleting time block:', error);
    }
  };

  return (
    <div className="time-blocking">
      <h2>Time Blocking Calendar</h2>
      <form onSubmit={handleSubmit} className="time-block-form">
        <input
          type="time"
          value={newBlock.timeSlot}
          onChange={(e) => setNewBlock({ ...newBlock, timeSlot: e.target.value })}
          required
        />
        <input
          type="text"
          value={newBlock.activity}
          onChange={(e) => setNewBlock({ ...newBlock, activity: e.target.value })}
          placeholder="Activity"
          required
        />
        <button type="submit">Add Block</button>
      </form>
      <div className="time-blocks">
        {timeBlocks.map((block) => (
          <div key={block.id} className="time-block">
            <span>{block.timeSlot}</span>
            <span>{block.activity}</span>
            <button onClick={() => handleDelete(block.id)}>Delete</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default TimeBlockingCalendar;
