import React, { useState, useEffect } from 'react';
import './RecurringTasks.css';

const WEEKDAYS = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'];
const FREQUENCIES = ['Daily', 'Weekly', 'Monthly'];

function RecurringTasks() {
  const [recurringTasks, setRecurringTasks] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newTask, setNewTask] = useState({
    title: '',
    description: '',
    frequency: 'Weekly',
    days: [],
    time: '09:00',
    category: '',
    priority: 'Medium'
  });

  useEffect(() => {
    // TODO: Load tasks from API
    const mockTasks = [
      {
        id: 1,
        title: 'Team Meeting',
        description: 'Weekly team sync',
        frequency: 'Weekly',
        days: ['Monday'],
        time: '10:00',
        category: 'Work',
        priority: 'High'
      },
      {
        id: 2,
        title: 'Exercise',
        description: 'Morning workout routine',
        frequency: 'Daily',
        days: [],
        time: '07:00',
        category: 'Health',
        priority: 'High'
      }
    ];
    setRecurringTasks(mockTasks);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const task = {
      id: Date.now(),
      ...newTask
    };
    setRecurringTasks([...recurringTasks, task]);
    setNewTask({
      title: '',
      description: '',
      frequency: 'Weekly',
      days: [],
      time: '09:00',
      category: '',
      priority: 'Medium'
    });
    setShowAddForm(false);
  };

  const handleDayToggle = (day) => {
    const days = newTask.days.includes(day)
      ? newTask.days.filter(d => d !== day)
      : [...newTask.days, day];
    setNewTask({ ...newTask, days });
  };

  const getPriorityColor = (priority) => {
    switch (priority.toLowerCase()) {
      case 'high':
        return 'var(--priority-high)';
      case 'medium':
        return 'var(--priority-medium)';
      case 'low':
        return 'var(--priority-low)';
      default:
        return 'var(--priority-medium)';
    }
  };

  const deleteTask = (taskId) => {
    setRecurringTasks(recurringTasks.filter(task => task.id !== taskId));
  };

  return (
    <div className="recurring-tasks-container">
      <div className="recurring-tasks-header">
        <div className="header-left">
          <h3>Recurring Tasks</h3>
          <div className="task-count">
            {recurringTasks.length} tasks
          </div>
        </div>
        <button 
          className="button-primary"
          onClick={() => setShowAddForm(true)}
        >
          + Add Recurring Task
        </button>
      </div>

      {showAddForm && (
        <div className="add-recurring-task-form">
          <form onSubmit={handleSubmit}>
            <div className="form-row">
              <div className="form-group">
                <label>Task Title</label>
                <input
                  type="text"
                  value={newTask.title}
                  onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                  required
                />
              </div>
              <div className="form-group">
                <label>Category</label>
                <input
                  type="text"
                  value={newTask.category}
                  onChange={(e) => setNewTask({ ...newTask, category: e.target.value })}
                  placeholder="e.g., Work, Personal, Health"
                />
              </div>
            </div>

            <div className="form-group">
              <label>Description</label>
              <textarea
                value={newTask.description}
                onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                rows="3"
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Frequency</label>
                <select
                  value={newTask.frequency}
                  onChange={(e) => setNewTask({ ...newTask, frequency: e.target.value })}
                >
                  {FREQUENCIES.map(freq => (
                    <option key={freq} value={freq}>{freq}</option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Time</label>
                <input
                  type="time"
                  value={newTask.time}
                  onChange={(e) => setNewTask({ ...newTask, time: e.target.value })}
                />
              </div>
              <div className="form-group">
                <label>Priority</label>
                <select
                  value={newTask.priority}
                  onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
                >
                  <option value="High">High</option>
                  <option value="Medium">Medium</option>
                  <option value="Low">Low</option>
                </select>
              </div>
            </div>

            {newTask.frequency === 'Weekly' && (
              <div className="form-group">
                <label>Days</label>
                <div className="weekday-selector">
                  {WEEKDAYS.map(day => (
                    <button
                      key={day}
                      type="button"
                      className={`day-button ${newTask.days.includes(day) ? 'selected' : ''}`}
                      onClick={() => handleDayToggle(day)}
                    >
                      {day.slice(0, 3)}
                    </button>
                  ))}
                </div>
              </div>
            )}

            <div className="form-actions">
              <button type="submit" className="button-primary">Create Task</button>
              <button 
                type="button" 
                className="button-secondary"
                onClick={() => setShowAddForm(false)}
              >
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="recurring-tasks-list">
        {recurringTasks.map(task => (
          <div key={task.id} className="recurring-task-card">
            <div className="task-card-header">
              <div className="task-card-title">
                <h4>{task.title}</h4>
                <span 
                  className="priority-badge"
                  style={{ backgroundColor: getPriorityColor(task.priority) }}
                >
                  {task.priority}
                </span>
              </div>
              <button 
                className="delete-button"
                onClick={() => deleteTask(task.id)}
              >
                ×
              </button>
            </div>
            
            <p className="task-description">{task.description}</p>
            
            <div className="task-details">
              <div className="detail-item">
                <span className="detail-label">Frequency:</span>
                <span className="detail-value">{task.frequency}</span>
              </div>
              <div className="detail-item">
                <span className="detail-label">Time:</span>
                <span className="detail-value">{task.time}</span>
              </div>
              {task.category && (
                <div className="detail-item">
                  <span className="detail-label">Category:</span>
                  <span className="detail-value">{task.category}</span>
                </div>
              )}
            </div>

            {task.frequency === 'Weekly' && task.days.length > 0 && (
              <div className="task-days">
                {WEEKDAYS.map(day => (
                  <span
                    key={day}
                    className={`day-indicator ${task.days.includes(day) ? 'active' : ''}`}
                  >
                    {day.slice(0, 1)}
                  </span>
                ))}
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default RecurringTasks;
