import React, { useState, useEffect } from 'react';
import './TimeBlocking.css';
import { getTimeBlocks, createTimeBlock, updateTimeBlock, deleteTimeBlock } from '../../utils/api';

function TimeBlockingCalendar() {
  const [timeBlocks, setTimeBlocks] = useState([]);
  const [newBlock, setNewBlock] = useState({
    timeSlot: '',
    activity: '',
    category: 'academic',
    description: '',
    date: new Date().toISOString().split('T')[0],
    quadrant: 'urgentImportant'
  });
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [view, setView] = useState('month');
  const [showSidebar, setShowSidebar] = useState(true);
  const [currentMonth, setCurrentMonth] = useState(new Date());

  const categories = {
    academic: {
      label: 'Academic',
      color: '#4285F4',
      icon: 'üìö'
    },
    personal: {
      label: 'Personal Development',
      color: '#0F9D58',
      icon: 'üíª'
    },
    social: {
      label: 'Social & Rest',
      color: '#DB4437',
      icon: 'üë•'
    }
  };

  const quadrants = {
    urgentImportant: {
      label: 'Urgent & Important',
      color: '#FF4444',
      icon: 'üî¥',
      description: 'Schedule immediately (today/tomorrow)'
    },
    importantNotUrgent: {
      label: 'Important but Not Urgent',
      color: '#FFBB33',
      icon: 'üü°',
      description: 'Schedule deep work blocks during peak energy hours'
    },
    urgentNotImportant: {
      label: 'Not Important but Urgent',
      color: '#00C851',
      icon: 'üü¢',
      description: 'Delegate or automate (use reminders, group them weekly)'
    },
    notUrgentNotImportant: {
      label: 'Not Important & Not Urgent',
      color: '#CCCCCC',
      icon: '‚ö™',
      description: 'Don\'t schedule‚Äîtrack only if truly necessary'
    }
  };

  const projectCategories = [
    { id: 'assignments', label: 'Assignments/Reports', icon: 'üìù' },
    { id: 'project', label: 'Final Year Project', icon: 'üéì' },
    { id: 'research', label: 'Reading/Research', icon: 'üìö' },
    { id: 'certificate', label: 'Certificate', icon: 'üèÜ' },
    { id: 'gantt', label: 'Visual Timeline', icon: 'üìä' }
  ];

  useEffect(() => {
    loadTimeBlocks();
  }, [selectedDate]);

  const loadTimeBlocks = async () => {
    try {
      const blocks = await getTimeBlocks();
      setTimeBlocks(blocks);
    } catch (error) {
      console.error('Error loading time blocks:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createTimeBlock(newBlock);
      setNewBlock({
        timeSlot: '',
        activity: '',
        category: 'academic',
        description: '',
        date: selectedDate,
        quadrant: 'urgentImportant'
      });
      loadTimeBlocks();
    } catch (error) {
      console.error('Error creating time block:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await deleteTimeBlock(id);
      loadTimeBlocks();
    } catch (error) {
      console.error('Error deleting time block:', error);
    }
  };

  const getDaysInMonth = (date) => {
    const year = date.getFullYear();
    const month = date.getMonth();
    const daysInMonth = new Date(year, month + 1, 0).getDate();
    const firstDayOfMonth = new Date(year, month, 1).getDay();
    const days = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < firstDayOfMonth; i++) {
      days.push(null);
    }

    // Add days of the month
    for (let i = 1; i <= daysInMonth; i++) {
      days.push(new Date(year, month, i));
    }

    return days;
  };

  const getWeekDates = () => {
    if (!selectedDate) return [];
    
    const dates = [];
    const currentDate = new Date(selectedDate);
    const day = currentDate.getDay();
    const diff = currentDate.getDate() - day;
    
    for (let i = 0; i < 7; i++) {
      const date = new Date(currentDate);
      date.setDate(diff + i);
      dates.push(date);
    }
    return dates;
  };

  const formatDate = (date) => {
    return date.toISOString().split('T')[0];
  };

  const isToday = (date) => {
    const today = new Date();
    return date.getDate() === today.getDate() &&
      date.getMonth() === today.getMonth() &&
      date.getFullYear() === today.getFullYear();
  };

  const isSelected = (date) => {
    return formatDate(date) === selectedDate;
  };

  const getBlocksForDate = (date) => {
    return timeBlocks.filter(block => block.date === formatDate(date));
  };

  const renderMonthView = () => {
    const days = getDaysInMonth(currentMonth);
    const weekDays = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];

    return (
      <div className="calendar-month-view">
        <div className="calendar-header">
          <button onClick={() => setCurrentMonth(new Date(currentMonth.setMonth(currentMonth.getMonth() - 1)))}>
            ‚Üê
          </button>
          <h2>{currentMonth.toLocaleDateString('en-US', { month: 'long', year: 'numeric' })}</h2>
          <button onClick={() => setCurrentMonth(new Date(currentMonth.setMonth(currentMonth.getMonth() + 1)))}>
            ‚Üí
          </button>
        </div>
        <div className="calendar-grid">
          {weekDays.map(day => (
            <div key={day} className="calendar-weekday">{day}</div>
          ))}
          {days.map((date, index) => (
            <div
              key={index}
              className={`calendar-day ${!date ? 'empty' : ''} ${isToday(date) ? 'today' : ''} ${isSelected(date) ? 'selected' : ''}`}
              onClick={() => date && setSelectedDate(formatDate(date))}
            >
              {date && (
                <>
                  <div className="day-number">{date.getDate()}</div>
                  <div className="day-events">
                    {getBlocksForDate(date).map(block => (
                      <div
                        key={block.id}
                        className="day-event"
                        style={{ 
                          backgroundColor: quadrants[block.quadrant].color,
                          borderLeft: `3px solid ${categories[block.category].color}`
                        }}
                      >
                        <span className="event-time">{block.timeSlot}</span>
                        <span className="event-title">{block.activity}</span>
                      </div>
                    ))}
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      </div>
    );
  };

  const renderWeekView = () => {
    const weekDates = getWeekDates();

    return (
      <div className="calendar-week-view">
        <div className="calendar-header">
          <button onClick={() => setSelectedDate(new Date(new Date(selectedDate).setDate(new Date(selectedDate).getDate() - 7)).toISOString().split('T')[0])}>
            ‚Üê
          </button>
          <h2>Week of {weekDates[0].toLocaleDateString('en-US', { month: 'short', day: 'numeric' })} - {weekDates[6].toLocaleDateString('en-US', { month: 'short', day: 'numeric', year: 'numeric' })}</h2>
          <button onClick={() => setSelectedDate(new Date(new Date(selectedDate).setDate(new Date(selectedDate).getDate() + 7)).toISOString().split('T')[0])}>
            ‚Üí
          </button>
        </div>
        <div className="week-grid">
          <div className="time-column">
            {Array.from({ length: 24 }, (_, i) => (
              <div key={i} className="time-slot">
                {`${i.toString().padStart(2, '0')}:00`}
              </div>
            ))}
          </div>
          {weekDates.map(date => (
            <div key={formatDate(date)} className="day-column">
              <div className="day-header">
                <div className="day-name">{date.toLocaleDateString('en-US', { weekday: 'short' })}</div>
                <div className="day-date">{date.getDate()}</div>
              </div>
              <div className="day-events">
                {getBlocksForDate(date).map(block => (
                  <div
                    key={block.id}
                    className="time-block"
                    style={{ 
                      backgroundColor: quadrants[block.quadrant].color,
                      borderLeft: `3px solid ${categories[block.category].color}`
                    }}
                  >
                    <div className="block-header">
                      <span className="category-icon">{categories[block.category].icon}</span>
                      <span className="quadrant-icon">{quadrants[block.quadrant].icon}</span>
                      <span className="activity">{block.activity}</span>
                    </div>
                    <div className="block-time">{block.timeSlot}</div>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  const renderDayView = () => {
    const date = new Date(selectedDate);
    const blocks = getBlocksForDate(date);

    return (
      <div className="calendar-day-view">
        <div className="calendar-header">
          <button onClick={() => setSelectedDate(new Date(new Date(selectedDate).setDate(new Date(selectedDate).getDate() - 1)).toISOString().split('T')[0])}>
            ‚Üê
          </button>
          <h2>{date.toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</h2>
          <button onClick={() => setSelectedDate(new Date(new Date(selectedDate).setDate(new Date(selectedDate).getDate() + 1)).toISOString().split('T')[0])}>
            ‚Üí
          </button>
        </div>
        <div className="day-timeline">
          {Array.from({ length: 24 }, (_, i) => (
            <div key={i} className="timeline-hour">
              <div className="hour-label">{`${i.toString().padStart(2, '0')}:00`}</div>
              <div className="hour-events">
                {blocks
                  .filter(block => {
                    const [hours] = block.timeSlot.split(':');
                    return parseInt(hours) === i;
                  })
                  .map(block => (
                    <div
                      key={block.id}
                      className="time-block"
                      style={{ 
                        backgroundColor: quadrants[block.quadrant].color,
                        borderLeft: `3px solid ${categories[block.category].color}`
                      }}
                    >
                      <div className="block-header">
                        <span className="category-icon">{categories[block.category].icon}</span>
                        <span className="quadrant-icon">{quadrants[block.quadrant].icon}</span>
                        <span className="activity">{block.activity}</span>
                      </div>
                      <div className="block-time">{block.timeSlot}</div>
                      {block.description && <div className="block-description">{block.description}</div>}
                      <button onClick={() => handleDelete(block.id)}>Delete</button>
                    </div>
                  ))}
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  };

  return (
    <div className="time-blocking">
      <div className={`sidebar ${showSidebar ? 'open' : ''}`}>
        <button className="toggle-sidebar" onClick={() => setShowSidebar(!showSidebar)}>
          {showSidebar ? '‚óÄ' : '‚ñ∂'}
        </button>
        
        <div className="sidebar-content">
          <div className="view-selector">
            <button 
              className={view === 'month' ? 'active' : ''} 
              onClick={() => setView('month')}
            >
              Month
            </button>
            <button 
              className={view === 'week' ? 'active' : ''} 
              onClick={() => setView('week')}
            >
              Week
            </button>
            <button 
              className={view === 'day' ? 'active' : ''} 
              onClick={() => setView('day')}
            >
              Day
            </button>
          </div>

          <form onSubmit={handleSubmit} className="time-block-form">
            <div className="form-group">
              <label>Time:</label>
              <input
                type="time"
                value={newBlock.timeSlot}
                onChange={(e) => setNewBlock({ ...newBlock, timeSlot: e.target.value })}
                required
              />
            </div>

            <div className="form-group">
              <label>Activity:</label>
              <input
                type="text"
                value={newBlock.activity}
                onChange={(e) => setNewBlock({ ...newBlock, activity: e.target.value })}
                placeholder="Activity"
                required
              />
            </div>

            <div className="form-group">
              <label>Category:</label>
              <select
                value={newBlock.category}
                onChange={(e) => setNewBlock({ ...newBlock, category: e.target.value })}
              >
                {Object.entries(categories).map(([key, { label }]) => (
                  <option key={key} value={key}>{label}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Quadrant:</label>
              <select
                value={newBlock.quadrant}
                onChange={(e) => setNewBlock({ ...newBlock, quadrant: e.target.value })}
              >
                {Object.entries(quadrants).map(([key, { label, icon }]) => (
                  <option key={key} value={key}>{icon} {label}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Project Category:</label>
              <select
                value={newBlock.projectCategory}
                onChange={(e) => setNewBlock({ ...newBlock, projectCategory: e.target.value })}
              >
                <option value="">Select Project Category</option>
                {projectCategories.map(category => (
                  <option key={category.id} value={category.id}>
                    {category.icon} {category.label}
                  </option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label>Description:</label>
              <textarea
                value={newBlock.description}
                onChange={(e) => setNewBlock({ ...newBlock, description: e.target.value })}
                placeholder="Description (optional)"
              />
            </div>

            <button type="submit">Add Block</button>
          </form>

          <div className="quadrant-legend">
            <h3>Quadrant Guide</h3>
            {Object.entries(quadrants).map(([key, { label, icon, color, description }]) => (
              <div key={key} className="legend-item" style={{ borderLeft: `4px solid ${color}` }}>
                <span className="legend-icon">{icon}</span>
                <div className="legend-text">
                  <strong>{label}</strong>
                  <p>{description}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="calendar-container">
        {view === 'month' && renderMonthView()}
        {view === 'week' && renderWeekView()}
        {view === 'day' && renderDayView()}
      </div>
    </div>
  );
}

export default TimeBlockingCalendar;
